    };
    const handleClick = () => {
        AddTodo(text);
    };

    return(
        <>
        <input 
        type="text"
        placeholder="Enter todo"
        value={text}
        onChange={handleChange}
        />
        <button onClick={handleClick}>Add todo</button>













